import React from "react";
import { useParams } from "react-router-dom";
//import { useParams } from "react-router-dom";
import WithConsumer from "../context/WithConsumer";
import PostMeta from "../partials/PostMeta";



const SinglePost = ({index,context}) => {
    const posts = context && context.posts ? context.posts : [];
    const item = posts()[index];  
    const params = useParams();
    let theContent = item.content.rendered;
    //stop page from redirecting to another url when a user refreshes the page
    
    return (
        <div>
            <h1>this is the slug: {params.slug}</h1>
            <PostMeta index={index}></PostMeta>
            <div className="post-content" dangerouslySetInnerHTML={{__html:theContent}}></div>
        
        </div>
    );
}

export default WithConsumer(SinglePost);
